function menu(){

  $('#menuScreen').show();

  $('#loginScreen').hide();

  $('#reglasScreen').hide();

  $('#fixtureScreen').hide();

  $('#participantesScreen').hide();

  $('#splashScreen').hide();

  $('#pronosticosScreen').hide();

}



function login(){

  let logged = localStorage.getItem('logged');

  if(logged){

    $('#participantesScreen').show();

    $('#menuScreen').hide();

    $('#reglasScreen').hide();

    $('#fixtureScreen').hide();

    $('#loginScreen').hide();

    $('#splashScreen').hide();

  }else{

    $('#loginScreen').show();

    $('#menuScreen').hide();

    $('#reglasScreen').hide();

    $('#fixtureScreen').hide();

    $('#participantesScreen').hide();

    $('#splashScreen').hide();

  }

}


